import React from 'react';
import { useInView } from 'react-intersection-observer';
import './Services.css';

const serviceData = [
  {
    id: 1,
    icon: 'ðŸ˜ï¸',
    title: 'Property Listings',
    description: 'Browse thousands of property listings updated daily. Find houses, apartments, and commercial spaces that match your specific requirements.'
  },
  {
    id: 2,
    icon: 'ðŸ’°',
    title: 'Financial Guidance',
    description: 'Get expert advice on mortgages, loans, and payment plans tailored to your financial situation for a smooth home buying experience.'
  },
  {
    id: 3,
    icon: 'ðŸ”',
    title: 'Property Valuation',
    description: 'Access accurate property valuations based on market trends, location analytics, and property features to make informed decisions.'
  },
  {
    id: 4,
    icon: 'ðŸ“',
    title: 'Legal Assistance',
    description: 'Our team of legal experts will guide you through the property paperwork and ensure all transactions are legal and compliant.'
  },
  {
    id: 5,
    icon: 'ðŸ ',
    title: 'Virtual Tours',
    description: 'Take virtual tours of properties from the comfort of your home to save time and narrow down your options before in-person visits.'
  },
  {
    id: 6,
    icon: 'ðŸ¤',
    title: 'After-Sale Support',
    description: 'We stay with you even after the purchase to help with renovation tips, property management, and other post-purchase needs.'
  }
];

const Services = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  return (
    <section id="services" className="services-section">
      <div className="container">
        <div className="section-header">
          <h2>Our Services</h2>
          <p>Comprehensive solutions for all your real estate needs</p>
        </div>

        <div ref={ref} className={`services-grid ${inView ? 'fade-in' : ''}`}>
          {serviceData.map((service) => (
            <div key={service.id} className="service-card">
              <div className="service-icon">{service.icon}</div>
              <h3 className="service-title">{service.title}</h3>
              <p className="service-description">{service.description}</p>
              <button className="service-button">Learn More</button>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Services;
